import React, { useState } from "react";
import './form.css';

function Form({ cardName, setCardName }){

  // const [number, setNumber] = useState("");
  // const [month, setMonth] = useState("");
  // const [year, setYear] = useState("");
  // const [cvc, setCvc] = useState("");
  return (
    <form className="input-form">
      <div>
        <label id='label'>CARDHOLDER NAME</label>
        <div>
          <input className='input-name-number' placeholder="e.g.Soumya raj" required value={cardName} onChange={(e) => setCardName (e.target.value)} />
        </div>
        <label id='not-valid'>This name is not valid !</label>
      </div>
      <div>
        <label id='label'>CARD NUMBER</label>
        <div>
          <input className='input-name-number' placeholder="e.g.1234 5678 9178 0000" required maxLength={19} onChange={(e) => setNumber(e.target.value)} />
        </div>
        <label id='not-valid'>This number is not valid !</label>
      </div>
      <div className="exp-cvc">
      <div>
        <label id='label'>EXP.DATE(MM/YY)</label>
        <div className="month-year">
            <div>
          <input className='mon-exp' placeholder="MM" onChange={(e) => setMonth(e.target.value)} />
          </div>
          <div>
          <input  className='mon-exp' placeholder="YY" onChange={(e) => setYear(e.target.value)} />
          </div>
        </div>
        <label id='not-valid'>This year is not valid !</label>
      </div>
      <div>
        <label id='label'>CVC</label>
        <div>
          <input className='cvc' placeholder="e.g.123" onChange={(e) => setCvc(e.target.value)} />
        </div>
        <label id='not-valid'>This cvc is not valid !</label>
      </div>
      </div>
      <button className="button">Confirm</button>
    </form>
  );
}
export default Form;
